<?phpnamespace App\Http\Livewire;use App\Models\User;use App\Models\Company;use Livewire\Component;use Livewire\WithPagination;use Illuminate\Support\Facades\Auth;class MenuUsers extends Component{    use WithPagination;    protected $paginationTheme="bootstrap";    public $search;    public function render()    {        $user = User::find(auth()->id());        if ($user->hasAnyRole(['Admin', 'CorporativoKP'])) {            $users=User::where('name', 'LIKE', '%'.$this->search. '%')                ->orwhere('email', 'LIKE', '%'.$this->search. '%')                ->orwhere('company_id', 'LIKE', '%'.$this->search. '%')                ->paginate(8);        }else{            $users=User::where(function($query) {                $query->where('name', 'LIKE', '%'.$this->search.'%')                    ->orWhere('email', 'LIKE', '%'.$this->search.'%')                    ->orWhere('company_id', 'LIKE', '%'.$this->search.'%');            })                ->where('company_id', $user->company_id) // AÃ±ade esta condiciÃ³n                ->paginate(8);        }        $role = $user->getRoleNames()->first();//        dump($user);        return view('livewire.menu-users',compact('users','role'));    }    public function limpiar_page(){        $this->resetPage();    }}