                                    <th>Componente</th>
                                    <th>Prioridad</th>
                                    <th>Comentarios</th>
                                </tr>
                            </thead>
                            <tbody>
                            @foreach ($inspections as $inspection)
                                @if ($inspection->condition==1)
                                    @foreach ($inspection->defect_track as $defect_track)
                                    <tr>
                                        <td>{{$defect_track->inspection_id}}</td>
                                        <td>{{$defect_track->component_catalog->name}}</td>
                                        <td>@switch($defect_track->priority)
                                            @case(1)
                                                Baja
                                                @break
                                            @case(2)
                                                Media
                                                @break
                                            @default
                                                Alta
                                            @endswitch
                                        </td>
                                        <td>{{$defect_track->comment}}</td>
                                    </tr>
                                    @endforeach
                                @endif
                            @endforeach
                            </tbody>
                        </table>
